import axios from "axios";

const Index = ({ posts }) => (
  <div>
    <h1>Index Page</h1>
    <ul>
      {posts.map((p) => (
        <li key={p.id}>{p.title}</li>
      ))}
    </ul>
  </div>
);

Index.getInitialProps = async () => {
  const res = await axios.get("https://jsonplaceholder.typicode.com/posts");
  const data = res.data;
  console.log(data);
  return { posts: data };
};

export default Index;
