import axios from "axios";

const Post = ({ id }) => {
  return <h1>POST ID IS {id}</h1>;
};

Post.getInitialProps = async ({ query }) => {
  const res = axios.get(
    `https://jsonplaceholder.typicode.com/comments?postId=${id}`
  );
  return { id: query.id, comments: res.data };
};

export default Post;
